dict.string:
query="$(cat /tmp/alfred-gif-query)"
function is_gif_browser_up() { pgrep AlfredGifBrowser > /dev/null }
function start_gif_browser() { open ./AlfredGifBrowser.app }
if ! is_gif_browser_up; then start_gif_browser cat << EOF {"items": [ {"title": "Press enter again to search for \"$query\"", "subtitle": "double-enter is needed for the first search", "arg": "$query"}]} EOF else curl -s -G 'http://localhost:9911/' \ --data-urlencode "query=$query" \ --data-urlencode "service=giphy" \ --data-urlencode "key=$giphy_key" fi

query="$(cat /tmp/alfred-gif-query)"
function is_gif_browser_up() { pgrep AlfredGifBrowser > /dev/null }
function start_gif_browser() { open ./AlfredGifBrowser.app }
if ! is_gif_browser_up; then start_gif_browser cat << EOF {"items": [ {"title": "Press enter again to search for \"$query\"", "subtitle": "double-enter is needed for the first search", "arg": "$query"}]} EOF else curl -s -G 'http://localhost:9911/' \ --data-urlencode "query=$query" \ --data-urlencode "service=tenor" fi

query="$(cat /tmp/alfred-gif-query)"
function is_gif_browser_up() { pgrep AlfredGifBrowser > /dev/null }
function start_gif_browser() { ./AlfredGifBrowser.app/Contents/MacOS/AlfredGifBrowser > /tmp/gif-browser.log 2>&1 & }
if ! is_gif_browser_up; then start_gif_browser cat << EOF {"items": [ {"title": "Press enter again to search for \"$query\"", "subtitle": "double-enter is needed for the first search", "arg": "$query"}]} EOF else curl -s -G 'http://localhost:9911/' \ --data-urlencode "query=$query" \ --data-urlencode "service=giphy_sticker" \ --data-urlencode "key=$giphy_key" fi

query="$(cat /tmp/alfred-gif-query)"
function is_gif_browser_up() { pgrep AlfredGifBrowser > /dev/null }
function start_gif_browser() { ./AlfredGifBrowser.app/Contents/MacOS/AlfredGifBrowser > /tmp/gif-browser.log 2>&1 & }
if ! is_gif_browser_up; then start_gif_browser cat << EOF {"items": [ {"title": "Press enter again to search for \"$query\"", "subtitle": "double-enter is needed for the first search", "arg": "$query"}]} EOF else curl -s -G 'http://localhost:9911/' \ --data-urlencode "query=$query" \ --data-urlencode "service=tenor_sticker" fi
